           console.log(cloumn.children.length)//取cloumn下的子元素个数
           for(let i=0;i<cloumn.children.length;i++)    
           {
               console.log(cloumn.children[i].name)//遍历cloumn下的子元素的name项
               console.log(cloumn.children[i].color)//遍历cloumn下的子元素的color项
           }
       }
   }
}
